angular.module('app.filters')
    .filter('numberFixedLen', function () {
        return function (n, len) {
            var num = parseInt(n, 10);
            len = parseInt(len, 10);
            if (isNaN(num) || isNaN(len)) {
                return n;
            }
            num = ''+num;
            while (num.length < len) {
                num = '0'+num;
            }
            return num;
        };
})

.filter('fractions', function() {
    return function(input) {
        return input.toFixed(2);
    };
});

angular.module('app.filters')
.filter('range', function() {
  return function(input, total) { 
    total = parseInt(total);
    for (var i=0; i<total; i++) {
      input.push(i);
    }
    return input;
  };
});
angular.module('app.filters')
.filter('unique', function() {
   return function(collection, keyname) {
      var output = [], 
          keys = [];

      angular.forEach(collection, function(item) {
          var key = item[keyname];
          if(keys.indexOf(key) === -1) {
              keys.push(key);
              output.push(item);
          }
      });

      return output;
   };
});
angular.module('app.filters')
.filter('youtubevideo',function($sce, $filter) {
    return function(url,height) {
        if(url){ 
            var p = /^(?:https?:\/\/)?(?:www\.)?(?:youtu\.be\/|youtube\.com\/(?:embed\/|v\/|watch\?v=|watch\?.+&v=))((\w|-){11})(?:\S+)?$/;
            var textUrl =  (String(url).match(p)) ? 'https://www.youtube.com/embed/'+(RegExp.$1) : false;
            if(textUrl) { 
                url = $sce.trustAsResourceUrl(textUrl);
                var html = "<iframe height='"+height+"' type='video/youtube' width='100%' class='embed-responsive-item' src='"+url+"?rel=0&modestbranding=0&autohide=1&showinfo=0&controls=0' allowfullscreen></iframe>";
                return $sce.trustAsHtml(html);
            }
        }    
        return '';
    };
});
