angular.module('app.services')
.factory('preload', function($q) {
    return function(url) {
        var deffered = $q.defer(),
            image = new Image();

        image.src = url;
        if (image.complete) {
            deffered.resolve();
        } else {
            image.addEventListener('load', function() {
                deffered.resolve();
            });
            image.addEventListener('error', function() {
                deffered.reject();
            });
        }
        return deffered.promise;
    }
});
